import React, {useState, useEffect} from 'react';

function SearchBar(props) {

    // const [error, setError] = useState(null);

    const [loaded, setLoaded] = useState(false);
    const [allCategories, setAll] = useState([]);

    useEffect(() => {
        fetch("http://127.0.0.1:8888/categories")
          .then(res => res.json())
          .then(
            data=>{
              setAll(data);
              setLoaded(true);
              console.log("fetched!")
            }
          )
      }, [])


    return (
        <div className="input-group search-bar">
            <div className="dropdown input-group-prepend">
                <button className="btn btn-secondary dropdown-toggle btn-lg" type="button"
                    data-bs-toggle="dropdown">
                    All
                </button>
                {loaded && allCategories}
                <ul className="dropdown-menu">
                    <li><a className="dropdown-item" href="#">Food</a></li>
                    <li><a className="dropdown-item" href="#">Books</a></li>
                    <li><a className="dropdown-item" href="#">Electronic</a></li>
                </ul>
            </div>
            <input type="text" className="form-control" />
            <button className="input-group-append btn btn-secondary btn-lg" type="button">Search<i
                className="bi bi-search"></i></button>
        </div>
    );
}

export default SearchBar
