import React, {useState, useEffect} from 'react';

function SearchBar(props) {

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);

    useEffect(() => {
        fetch("127.0.0.1:8888/")
          .then(res => res.json())
          .then(
            (result) => {
              setIsLoaded(true);
              setItems(result);
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              setIsLoaded(true);
              setError(error);
            }
          )
      }, [])

    return (
        <div class="input-group search-bar">
            <div class="dropdown input-group-prepend">
                <button class="btn btn-secondary dropdown-toggle btn-lg" type="button"
                    data-bs-toggle="dropdown">
                    All
                </button>
                <ul class="dropdown-menu">
                    <li><a class="dropdown-item" href="#">Food</a></li>
                    <li><a class="dropdown-item" href="#">Books</a></li>
                    <li><a class="dropdown-item" href="#">Electronic</a></li>
                </ul>
            </div>
            <input type="text" class="form-control" />
            <button class="input-group-append btn btn-secondary btn-lg" type="button">Search<i
                class="bi bi-search"></i></button>
        </div>
    );
}

export default SearchBar
